const { default: makeWASocket, DisconnectReason, useMultiFileAuthState, downloadMediaMessage } = require('@whiskeysockets/baileys');
const QRCode = require('qrcode');
const express = require('express');
const axios = require('axios');
const fs = require('fs');
const http = require('http');

// Servidor Express simples
const app = express();
app.use(express.json());

// Estado da aplica√ß√£o
let socket = null;
let qrCodeData = null;
let isConnected = false;
let connectionStatus = 'disconnected';
let userInfo = null;

// Rastrear mensagens enviadas automaticamente pelo sistema
let sentBySystem = new Set();

console.log('üöÄ Iniciando WhatsApp Service...');

// Fun√ß√£o para notificar o Flask
async function notifyFlask(endpoint, data) {
    try {
        await axios.post(`http://localhost:5000${endpoint}`, data, {
            headers: { 'Content-Type': 'application/json' },
            timeout: 3000
        });
    } catch (error) {
        // Ignorar erros de notifica√ß√£o
    }
}

// Conectar ao WhatsApp
async function connectToWhatsApp() {
    try {
        console.log('üì± Conectando ao WhatsApp...');

        // Usar pasta de auth local
        const { state, saveCreds } = await useMultiFileAuthState('./whatsapp_auth');

        socket = makeWASocket({
            auth: state,
            printQRInTerminal: false,
            // Remove logger config to use default
            browser: ['AsA WhatsApp Bot', 'Chrome', '1.0.0']
        });

        // Evento de atualiza√ß√£o de conex√£o
        socket.ev.on('connection.update', async (update) => {
            const { connection, lastDisconnect, qr } = update;

            if (qr) {
                console.log('üîó QR Code gerado!');
                qrCodeData = qr;
                connectionStatus = 'qr_ready';

                // Notificar Flask que QR est√° pronto
                await notifyFlask('/api/qr-updated', { qr_code: qr });
            }

            if (connection === 'close') {
                const shouldReconnect = (lastDisconnect?.error)?.output?.statusCode !== DisconnectReason.loggedOut;
                console.log('üîå Conex√£o fechada. Reconectar?', shouldReconnect);

                isConnected = false;
                connectionStatus = 'disconnected';
                qrCodeData = null;
                userInfo = null;

                await notifyFlask('/api/disconnected', { reason: 'connection_closed' });

                if (shouldReconnect) {
                    console.log('üîÑ Reconectando em 3 segundos...');
                    setTimeout(connectToWhatsApp, 3000);
                }
            } else if (connection === 'open') {
                console.log('‚úÖ WhatsApp conectado com sucesso!');
                isConnected = true;
                connectionStatus = 'connected';
                qrCodeData = null;
                userInfo = socket.user;

                await notifyFlask('/api/connected', { 
                    phone: socket.user?.id?.split(':')[0] || 'unknown',
                    user: socket.user 
                });
            }
        });

        // Salvar credenciais
        socket.ev.on('creds.update', saveCreds);

        // Receber mensagens
        socket.ev.on('messages.upsert', async (m) => {
            const message = m.messages[0];

            if (message.message) {
                const phoneNumber = message.key.remoteJid;

                // Extrair texto da mensagem
                let messageText = '';
                if (message.message.conversation) {
                    messageText = message.message.conversation;
                } else if (message.message.extendedTextMessage) {
                    messageText = message.message.extendedTextMessage.text;
                }

                if (messageText && phoneNumber) {
                    const cleanPhone = phoneNumber.replace('@s.whatsapp.net', '');

                    if (!message.key.fromMe) {
                        // Mensagem de cliente para n√≥s
                        console.log(`üì® Mensagem de ${cleanPhone}: ${messageText}`);

                        // Enviar para Flask processar
                        await notifyFlask('/api/message-received', {
                            phone: cleanPhone,
                            message: messageText,
                            contact_name: message.pushName || '',
                            message_id: message.key.id,
                            timestamp: new Date().toISOString(),
                            is_from_user: true
                        });
                    } else {
                        // Mensagem nossa para cliente - verificar se √© manual ou autom√°tica
                        const messageHash = `${cleanPhone}:${messageText}`;

                        if (sentBySystem.has(messageHash)) {
                            // Mensagem enviada automaticamente pelo sistema - remover do rastreamento
                            sentBySystem.delete(messageHash);
                            console.log(`ü§ñ Mensagem autom√°tica confirmada para ${cleanPhone}: ${messageText}`);
                        } else {
                            // Mensagem manual do humano - pausar IA
                            console.log(`üë§ Resposta manual detectada para ${cleanPhone}: ${messageText}`);

                            // Notificar Flask que humano respondeu (pausar IA)
                            await notifyFlask('/api/human-response-detected', {
                                phone: cleanPhone,
                                message: messageText,
                                timestamp: new Date().toISOString(),
                                is_from_user: false,
                                is_manual: true
                            });
                        }
                    }
                }
            }
        });

    } catch (error) {
        console.error('‚ùå Erro ao conectar:', error);
        connectionStatus = 'error';
        setTimeout(connectToWhatsApp, 5000);
    }
}

// API Routes
app.get('/status', (req, res) => {
    res.json({
        connected: isConnected,
        status: connectionStatus,
        qr_available: !!qrCodeData,
        user: userInfo
    });
});

app.get('/qr', async (req, res) => {
    if (qrCodeData) {
        try {
            const qrImage = await QRCode.toDataURL(qrCodeData);
            res.json({
                success: true,
                qr_code: qrCodeData,
                qr_image: qrImage
            });
        } catch (error) {
            res.status(500).json({ error: error.message });
        }
    } else {
        res.json({ success: false, message: 'QR Code n√£o dispon√≠vel' });
    }
});

app.post('/send-message', async (req, res) => {
    const { phone, message } = req.body;

    if (!isConnected || !socket) {
        return res.status(400).json({ error: 'WhatsApp n√£o conectado' });
    }

    try {
        let jid = phone;
        if (!phone.includes('@')) {
            // Limpar n√∫mero e adicionar @s.whatsapp.net
            const cleanPhone = phone.replace(/\D/g, '');
            jid = `${cleanPhone}@s.whatsapp.net`;
        }

        // Marcar como mensagem enviada pelo sistema
        const messageHash = `${phone}:${message}`;
        sentBySystem.add(messageHash);

        // Remover da lista ap√≥s 30 segundos (timeout de seguran√ßa)
        setTimeout(() => {
            sentBySystem.delete(messageHash);
        }, 30000);

        await socket.sendMessage(jid, { text: message });

        console.log(`ü§ñ Mensagem autom√°tica enviada para ${phone}: ${message}`);
        res.json({ success: true, message: 'Mensagem enviada' });
    } catch (error) {
        console.error('‚ùå Erro ao enviar mensagem:', error);
        res.status(500).json({ error: error.message });
    }
});

app.post('/set-typing', async (req, res) => {
    const { phone, typing = true } = req.body;

    if (!isConnected || !socket) {
        return res.status(400).json({ error: 'WhatsApp n√£o conectado' });
    }

    try {
        let jid = phone;
        if (!phone.includes('@')) {
            const cleanPhone = phone.replace(/\D/g, '');
            jid = `${cleanPhone}@s.whatsapp.net`;
        }

        await socket.sendPresenceUpdate(typing ? 'composing' : 'paused', jid);
        res.json({ success: true });
    } catch (error) {
        res.status(500).json({ error: error.message });
    }
});

// Iniciar servidor
const PORT = 3001;
const server = app.listen(PORT, () => {
    console.log(`üöÄ WhatsApp Service rodando na porta ${PORT}`);

    // Conectar automaticamente
    setTimeout(connectToWhatsApp, 1000);
});

// Graceful shutdown
process.on('SIGINT', async () => {
    console.log('üõë Encerrando WhatsApp Service...');
    if (socket) {
        await socket.logout();
    }
    server.close(() => {
        console.log('‚úÖ Servidor encerrado');
        process.exit(0);
    });
});

process.on('uncaughtException', (error) => {
    console.error('‚ùå Erro n√£o capturado:', error);
});

process.on('unhandledRejection', (error) => {
    console.error('‚ùå Promise rejeitada:', error);
});

static/css/custom.css.content:
/* Custom CSS for AsA WhatsApp System */

:root {
    --asa-primary: #25d366;
    --asa-secondary: #128c7e;
    --asa-accent: #075e54;
    --asa-light: #dcf8c6;
}

/* Custom WhatsApp-inspired colors */
.text-whatsapp {
    color: var(--asa-primary) !important;
}

.bg-whatsapp {
    background-color: var(--asa-primary) !important;
}

.btn-whatsapp {
    background-color: var(--asa-primary);
    border-color: var(--asa-primary);
    color: white;
}

.btn-whatsapp:hover {
    background-color: var(--asa-secondary);
    border-color: var(--asa-secondary);
    color: white;
}

/* QR Code display styling */
.qr-code-display {
    padding: 2rem;
    background: white;
    border-radius: 10px;
    box-shadow: 0 4px 6px rgba(0, 0, 0, 0.1);
    display: inline-block;
    text-align: center;
    min-width: 200px;
}

#qr-code-container {
    animation: fadeIn 0.5s ease-in;
}

@keyframes fadeIn {
    from { opacity: 0; transform: translateY(20px); }
    to { opacity: 1; transform: translateY(0); }
}

/* Connection status indicators */
.connection-indicator {
    position: relative;
}

.connection-indicator::before {
    content: '';
    position: absolute;
    top: 0;
    left: -10px;
    width: 8px;
    height: 8px;
    border-radius: 50%;
    background-color: var(--bs-success);
    animation: pulse 2s infinite;
}

.connection-indicator.disconnected::before {
    background-color: var(--bs-danger);
}

@keyframes pulse {
    0% { transform: scale(0.95); opacity: 1; }
    70% { transform: scale(1); opacity: 0.7; }
    100% { transform: scale(0.95); opacity: 1; }
}

/* Message bubbles for conversation view */
.message-bubble {
    max-width: 80%;
    padding: 0.75rem 1rem;
    margin: 0.5rem 0;
    border-radius: 18px;
    position: relative;
    word-wrap: break-word;
}

.message-from-user {
    background-color: var(--asa-light);
    color: #000;
    margin-left: auto;
    margin-right: 0;
    text-align: right;
}

.message-from-bot {
    background-color: var(--bs-light);
    color: var(--bs-dark);
    margin-left: 0;
    margin-right: auto;
}

.message-timestamp {
    font-size: 0.75rem;
    opacity: 0.7;
    margin-top: 0.25rem;
}

/* Typing indicator */
.typing-indicator {
    display: flex;
    align-items: center;
    padding: 0.5rem 1rem;
    background-color: var(--bs-light);
    border-radius: 18px;
    margin: 0.5rem 0;
    width: fit-content;
}

.typing-dots {
    display: flex;
    gap: 4px;
}

.typing-dot {
    width: 8px;
    height: 8px;
    border-radius: 50%;
    background-color: var(--bs-secondary);
    animation: typingAnimation 1.4s infinite ease-in-out;
}

.typing-dot:nth-child(2) {
    animation-delay: 0.2s;
}

.typing-dot:nth-child(3) {
    animation-delay: 0.4s;
}

@keyframes typingAnimation {
    0%, 60%, 100% {
        transform: translateY(0);
        opacity: 0.5;
    }
    30% {
        transform: translateY(-10px);
        opacity: 1;
    }
}

/* Admin dashboard enhancements */
.stats-card {
    transition: transform 0.2s ease-in-out, box-shadow 0.2s ease-in-out;
}

.stats-card:hover {
    transform: translateY(-2px);
    box-shadow: 0 4px 8px rgba(0, 0, 0, 0.1);
}

.stats-icon {
    font-size: 2.5rem;
    opacity: 0.8;
}

/* Response cards */
.response-card {
    transition: transform 0.2s ease-in-out;
    border-left: 4px solid var(--asa-primary);
}

.response-card:hover {
    transform: translateY(-2px);
}

.response-preview {
    max-height: 120px;
    overflow: hidden;
    position: relative;
}

.response-preview::after {
    content: '';
    position: absolute;
    bottom: 0;
    left: 0;
    height: 20px;
    width: 100%;
    background: linear-gradient(transparent, var(--bs-body-bg));
}

/* Loading states */
.loading-overlay {
    position: fixed;
    top: 0;
    left: 0;
    width: 100%;
    height: 100%;
    background-color: rgba(0, 0, 0, 0.5);
    display: flex;
    justify-content: center;
    align-items: center;
    z-index: 9999;
}

.loading-spinner {
    color: var(--asa-primary);
    font-size: 3rem;
}

/* Toast container positioning */
.toast-container {
    z-index: 1055;
}

.toast {
    min-width: 300px;
}

/* Form enhancements */
.form-floating .form-control:focus ~ label {
    color: var(--asa-primary);
}

.form-control:focus {
    border-color: var(--asa-primary);
    box-shadow: 0 0 0 0.2rem rgba(37, 211, 102, 0.25);
}

/* Button enhancements */
.btn-outline-primary:hover,
.btn-outline-primary:focus {
    background-color: var(--asa-primary);
    border-color: var(--asa-primary);
}

/* Table improvements */
.table-hover tbody tr:hover {
    background-color: rgba(37, 211, 102, 0.1);
}

/* Responsive improvements */
@media (max-width: 768px) {
    .qr-code-display {
        padding: 1rem;
        min-width: 150px;
    }

    .message-bubble {
        max-width: 90%;
    }

    .stats-card {
        margin-bottom: 1rem;
    }
}

/* Accessibility improvements */
.btn:focus,
.form-control:focus,
.nav-link:focus {
    outline: 2px solid var(--asa-primary);
    outline-offset: 2px;
}

/* Animation for page transitions */
.fade-in {
    animation: fadeIn 0.3s ease-in;
}

.slide-up {
    animation: slideUp 0.3s ease-out;
}

@keyframes slideUp {
    from {
        opacity: 0;
        transform: translateY(30px);
    }
    to {
        opacity: 1;
        transform: translateY(0);
    }
}

/* Status badges */
.badge {
    font-size: 0.75em;
    font-weight: 500;
}

/* Custom scrollbar for webkit browsers */
::-webkit-scrollbar {
    width: 8px;
}

::-webkit-scrollbar-track {
    background: var(--bs-light);
    border-radius: 4px;
}

::-webkit-scrollbar-thumb {
    background: var(--asa-primary);
    border-radius: 4px;
}

::-webkit-scrollbar-thumb:hover {
    background: var(--asa-secondary);
}

/* Print styles */
@media print {
    .navbar,
    .btn,
    .modal,
    .toast-container {
        display: none !important;
    }

    .container {
        max-width: none !important;
    }
}

static/js/main.js.content:
// Main JavaScript file for AsA WhatsApp System

// Global utility functions
function showAlert(message, type = 'info') {
    const alertHtml = `
        <div class="alert alert-${type} alert-dismissible fade show" role="alert">
            ${message}
            <button type="button" class="btn-close" data-bs-dismiss="alert"></button>
        </div>
    `;

    // Insert at the top of main content
    const mainContent = document.querySelector('main .container');
    if (mainContent) {
        mainContent.insertAdjacentHTML('afterbegin', alertHtml);
    }
}

// Auto-hide alerts after 5 seconds
document.addEventListener('DOMContentLoaded', function() {
    const alerts = document.querySelectorAll('.alert');
    alerts.forEach(alert => {
        setTimeout(() => {
            const bsAlert = new bootstrap.Alert(alert);
            bsAlert.close();
        }, 5000);
    });
});

// Connection status monitoring
class ConnectionMonitor {
    constructor() {
        this.isMonitoring = false;
        this.interval = null;
    }

    start() {
        if (this.isMonitoring) return;

        this.isMonitoring = true;
        this.interval = setInterval(() => {
            this.checkStatus();
        }, 10000); // Check every 10 seconds
    }

    stop() {
        if (this.interval) {
            clearInterval(this.interval);
            this.interval = null;
        }
        this.isMonitoring = false;
    }

    async checkStatus() {
        try {
            const response = await fetch('/connection_status');
            const data = await response.json();

            const statusElement = document.getElementById('connection-status');
            if (statusElement && data.is_connected) {
                // Reload page if connection status changed to connected
                if (!statusElement.innerHTML.includes('Conectado ao WhatsApp')) {
                    location.reload();
                }
            }
        } catch (error) {
            console.error('Error checking connection status:', error);
        }
    }
}

// Initialize connection monitor on relevant pages
const connectionMonitor = new ConnectionMonitor();
if (document.getElementById('connection-status')) {
    connectionMonitor.start();
}

// QR Code generation
async function generateQRCode() {
    const button = document.getElementById('generate-qr-btn');
    if (!button) return;

    const originalText = button.innerHTML;
    button.innerHTML = '<i class="fas fa-spinner fa-spin me-2"></i>Gerando...';
    button.disabled = true;

    try {
        const response = await fetch('/generate_qr');
        const data = await response.json();

        if (!data.success) {
            throw new Error(data.error || 'Erro ao gerar QR Code');
        }

        const qrDisplay = document.getElementById('qr-display');
        if (qrDisplay && data.qr_image) {
            qrDisplay.innerHTML = `
                <div class="qr-code-display text-center">
                    <img src="${data.qr_image}" alt="QR Code" class="qr-image mb-3" style="max-width: 300px; width: 100%;">
                    <p class="mt-2 mb-1 text-dark"><strong>C√≥digo gerado!</strong></p>
                    <small class="text-muted">Escaneie com seu WhatsApp</small>
                </div>
            `;
        } else {
            throw new Error('QR Code n√£o dispon√≠vel');
        }

        // Start monitoring for connection
        connectionMonitor.start();

        showAlert('C√≥digo QR gerado! Escaneie com seu WhatsApp.', 'success');

        // Mostrar bot√£o de simular escaneamento para teste
        const simulateBtn = document.getElementById('simulate-scan-btn');
        if (simulateBtn) {
            simulateBtn.style.display = 'inline-block';
        }

    } catch (error) {
        console.error('Error generating QR code:', error);
        showAlert('Erro ao gerar c√≥digo QR. Tente novamente.', 'danger');
    } finally {
        button.innerHTML = originalText;
        button.disabled = false;
    }
}

// Test message functionality
async function sendTestMessage() {
    const messageInput = document.getElementById('test-message');
    const button = document.getElementById('send-test-btn');

    if (!messageInput || !button) return;

    const message = messageInput.value.trim();
    if (!message) {
        showAlert('Digite uma mensagem para testar.', 'warning');
        return;
    }

    const originalText = button.innerHTML;
    button.innerHTML = '<i class="fas fa-spinner fa-spin me-2"></i>Enviando...';
    button.disabled = true;

    try {
        const response = await fetch('/api/simulate_message', {
            method: 'POST',
            headers: {
                'Content-Type': 'application/json',
            },
            body: JSON.stringify({
                phone: '5511999999999',
                message: message,
                name: 'Usu√°rio Teste'
            })
        });

        const data = await response.json();

        if (data.status === 'success') {
            showAlert('Mensagem de teste enviada com sucesso! Verifique o painel admin.', 'success');
            messageInput.value = '';
        } else {
            throw new Error('Erro na resposta do servidor');
        }

    } catch (error) {
        console.error('Error sending test message:', error);
        showAlert('Erro ao enviar mensagem de teste.', 'danger');
    } finally {
        button.innerHTML = originalText;
        button.disabled = false;
    }
}

// Simulate QR scan for testing
async function simulateScan() {
    const button = document.getElementById('simulate-scan-btn');
    if (!button) return;

    const originalText = button.innerHTML;
    button.innerHTML = '<i class="fas fa-spinner fa-spin me-2"></i>Conectando...';
    button.disabled = true;

    try {
        const response = await fetch('/simulate_scan');
        const data = await response.json();

        if (data.status === 'connected') {
            showAlert('WhatsApp conectado com sucesso!', 'success');
            setTimeout(() => {
                location.reload();
            }, 1500);
        } else {
            throw new Error('Erro na conex√£o');
        }

    } catch (error) {
        console.error('Error simulating scan:', error);
        showAlert('Erro ao simular escaneamento.', 'danger');
    } finally {
        button.innerHTML = originalText;
        button.disabled = false;
    }
}

// Event listeners
document.addEventListener('DOMContentLoaded', function() {
    // QR Code generation button
    const generateQRBtn = document.getElementById('generate-qr-btn');
    if (generateQRBtn) {
        generateQRBtn.addEventListener('click', generateQRCode);
    }

    // Test message button
    const sendTestBtn = document.getElementById('send-test-btn');
    if (sendTestBtn) {
        sendTestBtn.addEventListener('click', sendTestMessage);
    }

    // Simulate scan button
    const simulateScanBtn = document.getElementById('simulate-scan-btn');
    if (simulateScanBtn) {
        simulateScanBtn.addEventListener('click', simulateScan);
    }

    // Auto-focus on password field in login page
    const passwordField = document.getElementById('password');
    if (passwordField && window.location.pathname.includes('login')) {
        passwordField.focus();
    }

    // Form validation for response forms
    const responseForm = document.querySelector('form[action*="responses"]');
    if (responseForm) {
        responseForm.addEventListener('submit', function(e) {
            const keyword = document.getElementById('trigger_keyword');
            const response = document.getElementById('response_text');

            if (keyword && keyword.value.trim().length < 2) {
                e.preventDefault();
                showAlert('A palavra-chave deve ter pelo menos 2 caracteres.', 'warning');
                keyword.focus();
                return;
            }

            if (response && response.value.trim().length < 5) {
                e.preventDefault();
                showAlert('A resposta deve ter pelo menos 5 caracteres.', 'warning');
                response.focus();
                return;
            }
        });
    }
});

// Real-time updates for admin dashboard
class DashboardUpdater {
    constructor() {
        this.updateInterval = null;
    }

    start() {
        if (this.updateInterval) return;

        // Update every 30 seconds
        this.updateInterval = setInterval(() => {
            this.updateStats();
        }, 30000);
    }

    stop() {
        if (this.updateInterval) {
            clearInterval(this.updateInterval);
            this.updateInterval = null;
        }
    }

    async updateStats() {
        // This would fetch updated statistics
        // For now, we'll just refresh the page occasionally
        if (Math.random() < 0.1) { // 10% chance to refresh
            location.reload();
        }
    }
}

// Initialize dashboard updater on admin pages
if (window.location.pathname.includes('/admin/')) {
    const dashboardUpdater = new DashboardUpdater();
    dashboardUpdater.start();
}

